import React, { useState } from "react";

const NavBar = () => {
  const [open, setOpen] = useState(false);

  const toggleMenu = () => {
    setOpen(!open);
  };

  return (
    <nav className="w-full">
      <div className="flex bg-amber-300 justify-between items-center p-5">
        {/* Logo */}
        <div>
          <h1 className="uppercase font-bold text-3xl">Ecommerce</h1>
        </div>

        {/* Menu Icon */}
        <div className="md:hidden">
          <button onClick={toggleMenu}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              strokeWidth={1.5}
              stroke="currentColor"
              className="w-6 h-6"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5"
              />
            </svg>
          </button>
        </div>
      </div>

      {/* Responsive Menu */}
      {open && (
        <div className="md:hidden bg-gray-200 p-2">
          <ul className="flex flex-col items-center">
            <li className="py-2">Categories</li>
            <li className="py-2">Sales</li>
            <li className="py-2">Clearance</li>
            <li className="py-2">New Stock</li>
            <li className="py-2">Trending</li>
          </ul>
        </div>
      )}
    </nav>
  );
};

export default NavBar;
